function generateHeaderInnerHtml(region) {
    var html = ''
    html += '<h1 class="header-label">' + region + 'テイクアウト' + '</h1>'
    html += '<a href="../index.html">Back</a>'

    return html
}

function generateShopItemHtml(shops) {
    var html = ''
    shops.forEach(function(shop) {
        var open_hours = shop.open_hours != '' ? shop.open_hours : '??〜??'
        html += '<li class="shop-item">'
        html +=     '<h4>'
        html +=         '<a href="' + shop.url + '" rel="nofollow" target="_blank">' + shop.name + '</a><br>'
        html +=         '<span class="shop-genre">' + shop.genre + '</span>'
        html +=     '</h4>'
        html +=     '<p>'
        html +=         '<span>' + shop.address + '</span><br>'
        html +=         '<span>' + open_hours + '</span><br>'
        html +=         '<a href="tel:' + shop.tel + '">' + shop.tel + '</a>'
        html +=     '</p>'
        html += '</li>'
    })

    return html
}
