function generateHeaderInnerHtml(region) {
    var html = ''
    html += '<h1 class="header-label">' + region + 'テイクアウト' + '</h1>'
    html += '<a href="../index.html">Back</a>'

    return html
}

function generateShopItemHtml(shops) {
    var html = ''
    shops.forEach(function(shop) {
        var default_image = 'https://www.kargomaster.com/pub/media/catalog/product/placeholder/default/sorry-image-not-available.jpg'
        var image = shop.image_url != '' ? shop.image_url : default_image
        var fallback_image = shop.fallback_image != '' ? shop.fallback_image : default_image
        var open_hours = shop.open_hours != '' ? shop.open_hours : '??〜??'

        html += '<li class="shop-item">'
        html +=     '<div class="shop-item-grid">'
        html +=         '<a href="' + fallback_image + '" target="_blank">'
        html +=             '<picture>'
        html +=                 '<source type="image/webp" media="(min-width: 150px)" srcset="' + image + '">'
        html +=                 '<img src="' + fallback_image + '" class="shop-image" alt="' + shop.name + '">'
        html +=             '</picture>'
        html +=         '</a>'
        html +=     '</div>'
        html +=     '<div class="shop-item-grid">'
        html +=         '<h4>'
        html +=             '<a href="' + shop.url + '" rel="nofollow noopener" target="_blank">' + shop.name + '</a><br>'
        html +=             '<span class="shop-genre">' + shop.genre + '</span>'
        html +=         '</h4>'
        html +=         '<p>'
        html +=             '<span>' + shop.address + '</span><br>'
        html +=             '<span>' + open_hours + '</span><br>'
        html +=             '<a href="tel:' + shop.tel + '">' + shop.tel + '</a>'
        html +=         '</p>'
        html +=     '</div>'
        html += '</li>'
    })

    return html
}
