function generateHeaderInnerHtml(region) {
    var html = ''
    html += '<h1 class="header-label">' + region + 'テイクアウト' + '</h1>'
    html += '<a href="../index.html">Back</a>'

    return html
}

function generateShopItemHtml(shops, image_dir) {
    var html = ''
    shops.forEach(function(shop) {
        var default_image_name = 'image-not-available',
            thumbnail = shop.image_name != '' ?
                image_dir + '/' + shop.image_name + '_thumbnail.webp' :
                image_dir + '/' + default_image_name + '_thumbnail.webp',
            fallback_thumbnail = shop.image_name != '' ?
                image_dir + '/' + shop.image_name + '_thumbnail.png' :
                image_dir + '/' + default_image_name + '_thumbnail.png',
            image = shop.image_name != '' ?
                image_dir + '/' + shop.image_name + '.png' :
                image_dir + '/' + default_image_name + '.png',
            open_hours = shop.open_hours != '' ? shop.open_hours : '??〜??'

        html += '<li class="shop-item">'
        html +=     '<div class="shop-item-grid">'
        html +=         '<a href="' + image + '" target="_blank">'
        html +=             '<picture>'
        html +=                 '<source type="image/webp" media="(min-width: 150px)" srcset="' + thumbnail + '">'
        html +=                 '<img src="' + fallback_thumbnail + '" class="shop-image" alt="' + shop.name + '">'
        html +=             '</picture>'
        html +=         '</a>'
        html +=     '</div>'
        html +=     '<div class="shop-item-grid">'
        html +=         '<h4>'
        html +=             '<a href="' + shop.url + '" rel="nofollow noopener" target="_blank">' + shop.name + '</a><br>'
        html +=             '<span class="shop-genre">' + shop.genre + '</span>'
        html +=         '</h4>'
        html +=         '<p>'
        html +=             '<span>' + shop.address + '</span><br>'
        html +=             '<span>' + open_hours + '</span><br>'
        html +=             '<a href="tel:' + shop.tel + '">' + shop.tel + '</a>'
        html +=         '</p>'
        html +=     '</div>'
        html += '</li>'
    })

    return html
}
